--  DDL for Trigger TRG_COMPANY_ARCHIVE_DELETE
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "KAROL"."TRG_COMPANY_ARCHIVE_DELETE" 
BEFORE DELETE ON company
FOR EACH ROW
-- Trigger BEFORE DELETE umożliwia dostęp do wartości :OLD przed usunięciem rekordu,
-- co pozwala na zapisanie pełnego rekordu do tabeli COMPANY_ARCHIVE.
BEGIN
    -------------------------------
    -- 1. Archiwizacja rekordu --
    -------------------------------
    INSERT INTO company_archive (company_id, name, ticker, sector, country, website, deleted_at, deleted_by)
    VALUES (
        :OLD.company_id,
        :OLD.name,
        :OLD.ticker,
        :OLD.sector,
        :OLD.country,
        :OLD.website,
        SYSTIMESTAMP,
        SYS_CONTEXT('USERENV','SESSION_USER')
    );

    -------------------------------
    -- 2. Logowanie operacji DELETE --
    -------------------------------
    log_company_delete(
         p_company_id    => :OLD.company_id,
         p_status        => 'OK',
         p_operation     => 'DELETE',
         p_table_name    => 'COMPANY',
         p_action_detail => 'Usunięto rekord COMPANY_ID=' || TO_CHAR(:OLD.company_id),
         p_message       => 'Rekord przeniesiony do COMPANY_ARCHIVE'
    );
EXCEPTION
    WHEN OTHERS THEN
       -- Jeśli archiwizacja (główna operacja) się nie powiedzie, rzucamy wyjątek, by przerwać usunięcie.
       RAISE;
END;
/
ALTER TRIGGER "KAROL"."TRG_COMPANY_ARCHIVE_DELETE" ENABLE;
