--------------------------------------------------------
--  DDL for View V_CURRENT_PORTFOLIO_VALUE
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_CURRENT_PORTFOLIO_VALUE" ("INVESTOR_ID", "INVESTOR_NAME", "INVESTOR_EMAIL", "CLIENT_CODE", "TOTAL_VALUE_PLN") AS
  SELECT
  p.investor_id,
  i.name        AS investor_name,
  i.email       AS investor_email,
  i.client_code AS client_code,
  SUM(
       p.shares *
       (
         SELECT s.close_price
         FROM STOCKPRICE s
         WHERE s.company_id = p.company_id
         ORDER BY s.trade_date DESC
         FETCH FIRST 1 ROWS ONLY
       )
       *
       (
         SELECT e.rate_to_pln
         FROM EXCHANGERATE e
         WHERE e.currency = 'USD'
         ORDER BY e.rate_date DESC
         FETCH FIRST 1 ROWS ONLY
       )
     ) AS total_value_pln
FROM PORTFOLIO p
JOIN INVESTOR i ON p.investor_id = i.investor_id
GROUP BY p.investor_id, i.name, i.email, i.client_code
;
