--------------------------------------------------------
--  DDL for View V_PORTFOLIO_CUMULATIVE
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_PORTFOLIO_CUMULATIVE" ("INVESTOR_ID", "SNAPSHOT_DATE", "TOTAL_VALUE_PLN", "CUMULATIVE_VALUE") AS
  SELECT investor_id, snapshot_date, total_value_pln, cumulative_value
FROM (
  SELECT
    investor_id,
    snapshot_date,
    total_value_pln,
    SUM(total_value_pln) OVER (
      PARTITION BY investor_id
      ORDER BY snapshot_date
      ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW
    ) AS cumulative_value,
    ROW_NUMBER() OVER (
      PARTITION BY investor_id, snapshot_date
      ORDER BY total_value_pln DESC
    ) AS rn
  FROM portfoliosnapshot
  WHERE total_value_pln IS NOT NULL
)
WHERE rn = 1
ORDER BY investor_id, snapshot_date
;
--------------------------------------------------------
--  DDL for View V_PORTFOLIO_DAILY_CHANGE
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_PORTFOLIO_DAILY_CHANGE" ("INVESTOR_ID", "SNAPSHOT_DATE", "TOTAL_VALUE_PLN", "PREVIOUS_VALUE", "DAILY_CHANGE") AS
  SELECT
  investor_id,
  snapshot_date,
  total_value_pln,
  LAG(total_value_pln) OVER (
    PARTITION BY investor_id
    ORDER BY snapshot_date
  ) AS previous_value,
  total_value_pln - LAG(total_value_pln) OVER (
    PARTITION BY investor_id
    ORDER BY snapshot_date
  ) AS daily_change
FROM portfoliosnapshot
WHERE total_value_pln IS NOT NULL
;
