  SELECT
    p.investor_id,
    p.company_id,
    c.name AS company_name,
    c.ticker,
    p.shares,
    p.avg_price,
    latest.trade_date,
    latest.close_price AS latest_close_usd,
    ROUND(latest.close_price * get_exchange_rate('USD'), 2) AS latest_close_pln,
    ROUND(p.shares * latest.close_price * get_exchange_rate('USD'), 2) AS total_value_pln
FROM PORTFOLIO p
JOIN COMPANY c ON p.company_id = c.company_id
JOIN (
    SELECT company_id, trade_date, close_price
    FROM (
        SELECT company_id, trade_date, close_price FROM STOCKPRICE
        UNION ALL
        SELECT company_id, trade_date, close_price FROM STOCKPRICE_ARCHIVE
    )
    WHERE (company_id, trade_date) IN (
        SELECT company_id, MAX(trade_date)
        FROM (
            SELECT company_id, trade_date FROM STOCKPRICE
            UNION ALL
            SELECT company_id, trade_date FROM STOCKPRICE_ARCHIVE
        )
        GROUP BY company_id
    )
) latest ON latest.company_id = p.company_id
