--  DDL for Procedure BACKUP_SCHEMA_TO_FILE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "BACKUP_SCHEMA_TO_FILE" IS
  l_file UTL_FILE.FILE_TYPE;
  l_line CLOB;
BEGIN
  -- otwieramy plik
  l_file := UTL_FILE.FOPEN('BACKUP_DIR', 'schema_backup_' || TO_CHAR(SYSDATE, 'YYYYMMDD_HH24MISS') || '.sql', 'W', 32767);

  -- Tabele
  FOR obj IN (
    SELECT object_name FROM user_objects WHERE object_type = 'TABLE'
  ) LOOP
    l_line := DBMS_METADATA.GET_DDL('TABLE', obj.object_name);
    UTL_FILE.PUT_LINE(l_file, l_line || ';');
  END LOOP;

  -- Widoki, procedury, funkcje, pakiety
  FOR obj IN (
    SELECT object_name, object_type
    FROM user_objects
    WHERE object_type IN ('VIEW', 'PROCEDURE', 'FUNCTION', 'PACKAGE')
  ) LOOP
    l_line := DBMS_METADATA.GET_DDL(obj.object_type, obj.object_name);
    UTL_FILE.PUT_LINE(l_file, CHR(10) || l_line || ';');
  END LOOP;

  -- Dane z tabel
  FOR t IN (
    SELECT table_name FROM user_tables
  ) LOOP
    FOR r IN (
      SELECT * FROM (SELECT * FROM user_tab_columns WHERE table_name = t.table_name)
    ) LOOP
      NULL; -- tu ewentualnie można dodać generację INSERTów
    END LOOP;
    -- tutaj możesz zamiast PL/SQL użyć osobnej procedury eksportującej INSERTy np. jak wcześniej
  END LOOP;

  UTL_FILE.FCLOSE(l_file);
  DBMS_OUTPUT.PUT_LINE('✅ Backup zakończony.');
EXCEPTION
  WHEN OTHERS THEN
    IF UTL_FILE.IS_OPEN(l_file) THEN
      UTL_FILE.FCLOSE(l_file);
    END IF;
    DBMS_OUTPUT.PUT_LINE('❌ Błąd backupu: ' || SQLERRM);
END;

