--------------------------------------------------------
--  DDL for Procedure BACKUP_SCHEMA_VIA_DATAPUMP
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "BACKUP_SCHEMA_VIA_DATAPUMP" AS
  h1         NUMBER;
  v_jobname  VARCHAR2(50);
  v_dumpfile VARCHAR2(100);
  v_logfile  VARCHAR2(100);
BEGIN
  -- Nazwy plików i joba z datą/godziną
  v_jobname  := 'SCHEMA_EXPORT_' || TO_CHAR(SYSDATE, 'YYYYMMDD_HH24MISS');
  v_dumpfile := 'backup_' || TO_CHAR(SYSDATE, 'YYYYMMDD_HH24MISS') || '.dmp';
  v_logfile  := 'backup_' || TO_CHAR(SYSDATE, 'YYYYMMDD_HH24MISS') || '.log';

  -- Inicjacja eksportu
  h1 := DBMS_DATAPUMP.OPEN(
    operation   => 'EXPORT',
    job_mode   => 'SCHEMA',
    job_name   => v_jobname,
    version    => 'LATEST');

  -- Dump file
  DBMS_DATAPUMP.ADD_FILE(
    handle     => h1,
    filename   => v_dumpfile,
    directory  => 'BACKUP_DIR',
    filetype   => DBMS_DATAPUMP.KU$_FILE_TYPE_DUMP_FILE);

  -- Log file
  DBMS_DATAPUMP.ADD_FILE(
    handle     => h1,
    filename   => v_logfile,
    directory  => 'BACKUP_DIR',
    filetype   => DBMS_DATAPUMP.KU$_FILE_TYPE_LOG_FILE);

  -- Eksport schematu
  DBMS_DATAPUMP.METADATA_FILTER(
    handle => h1,
    name   => 'SCHEMA_LIST',
    value  => '''KAROL''');

  DBMS_DATAPUMP.START_JOB(h1);
  DBMS_DATAPUMP.DETACH(h1);

