set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "EXPORT_LOG_TO_CSV" AS
  v_file  UTL_FILE.FILE_TYPE;
  v_line  VARCHAR2(4000);
  v_message VARCHAR2(4000);
BEGIN
  -- Otwarcie pliku CSV, w BACKUP_DIR
  v_file := UTL_FILE.FOPEN('BACKUP_DIR', 'log_backup.csv', 'W', 4000);

  -- Nagłówek CSV
  UTL_FILE.PUT_LINE(v_file, 'LOG_ID,LOG_TIME,STATUS,OPERATION,USER_NAME,TABLE_NAME,ACTION_DETAIL,MESSAGE');

  -- Iteracja po LOG
  FOR rec IN (
    SELECT LOG_ID,
           LOG_TIME,
           STATUS,
           OPERATION,
           USER_NAME,
           TABLE_NAME,
           ACTION_DETAIL,
           DBMS_LOB.SUBSTR(MESSAGE, 4000, 1) AS MESSAGE
    FROM LOG
    ORDER BY LOG_TIME
  ) LOOP
    -- Zamiana przcinków
    v_line := rec.LOG_ID || ',' ||
              TO_CHAR(rec.LOG_TIME, 'YYYY-MM-DD HH24:MI:SS') || ',' ||
              rec.STATUS || ',' ||
              rec.OPERATION || ',' ||
              rec.USER_NAME || ',' ||
              rec.TABLE_NAME || ',' ||
              rec.ACTION_DETAIL || ',' ||
              rec.MESSAGE;

    UTL_FILE.PUT_LINE(v_file, v_line);
