      (p_period_type = 'QUARTER' AND TO_CHAR(snapshot_date, 'YYYY') || '-Q' || TO_CHAR(CEIL(TO_NUMBER(TO_CHAR(snapshot_date, 'MM')) / 3)) = p_period_label) OR
      (p_period_type = 'YEAR'    AND TO_CHAR(snapshot_date, 'YYYY') = p_period_label)
    );

  RETURN v_avg;
